function updateCountdown(){const t=new Date,e=targetDate-t,n=Math.floor(e/864e5),o=Math.floor(e%864e5/36e5),d=Math.floor(e%36e5/6e4),a=Math.floor(e%6e4/1e3);document.getElementById("days").textContent=`${n} Days`,document.getElementById("hours").textContent=`${o} Hours`,document.getElementById("minutes").textContent=`${d} Minutes`,document.getElementById("seconds").textContent=`${a} Seconds`,e<0&&(clearInterval(interval),countdown.innerHTML="<h1>Launched!</h1>")}const countdown=document.getElementById("countdown"),targetDate=new Date("June 15, 2024 00:00:00");updateCountdown();const interval=setInterval(updateCountdown,1e3);